    @Override
    public void onUpgrade(SQLiteDatabase db, int oldVersion, int newVersion) {
        // Aquí puedes actualizar tu estructura si cambias de versión
        db.execSQL("DROP TABLE IF EXISTS Detalle_Pedido");
        db.execSQL("DROP TABLE IF EXISTS Pedido");
        db.execSQL("DROP TABLE IF EXISTS Metodo_Pago");
        db.execSQL("DROP TABLE IF EXISTS Usuario");
        db.execSQL("DROP TABLE IF EXISTS Distrito");
        db.execSQL("DROP TABLE IF EXISTS Producto");
        db.execSQL("DROP TABLE IF EXISTS Categoria");
        onCreate(db);
    }

    public List<Producto> obtenerTodosLosProductos() {
        List<Producto> lista = new ArrayList<>();
        SQLiteDatabase db = this.getReadableDatabase();
        Cursor cursor = db.rawQuery("SELECT * FROM Producto", null);

        if (cursor.moveToFirst()) {
            do {
                Producto producto = new Producto(
                        cursor.getInt(cursor.getColumnIndexOrThrow("ID_Producto")),
                        cursor.getString(cursor.getColumnIndexOrThrow("Nombre")),
                        cursor.getString(cursor.getColumnIndexOrThrow("Descripcion")),
                        cursor.getDouble(cursor.getColumnIndexOrThrow("Precio")),
                        cursor.getInt(cursor.getColumnIndexOrThrow("Stock")),
                        cursor.getString(cursor.getColumnIndexOrThrow("Tamanio")),
                        cursor.getString(cursor.getColumnIndexOrThrow("ImagenURL")),
                        cursor.getInt(cursor.getColumnIndexOrThrow("ID_Categoria"))
                );
                lista.add(producto);
            } while (cursor.moveToNext());
        }

        cursor.close();
        db.close();
        return lista;
    }
